import axios from 'axios';

// URL base para la API
const API_URL = 'http://localhost:5199';

// Configuración base de axios
const baseConfig = {
  baseURL: API_URL,
  headers: {
    'Accept': 'application/json',
  }
};

// Instancia de axios
const api = axios.create(baseConfig);

// -------- Funciones relacionadas con WeatherForecast --------
export const obtenerClima = () => api.get('/WeatherForecast');

// -------- Agregar más rutas según sea necesario --------
export const obtenerOtroEndpoint = () => api.get('/otro-endpoint');
export const obtenerDatosPorId = (id) => api.get(`/datos/${id}`);
export const crearNuevoRecurso = (data) => api.post('/nuevo-recurso', data);

// Exportar la instancia para su uso en otros módulos
export { api };
export default api;
