import React from 'react';
import { Container, Row, Col, Card, CardBody, CardTitle, Badge, Table } from 'reactstrap';
import { Cloud, Thermometer, Wind } from 'react-feather';
import useApi from './useApi';

const WeatherForecastPage = () => {
  const { data: weatherData, loading, error } = useApi(`${process.env.REACT_APP_API_BASE_URL}/WeatherForecast`);

  const getTemperatureIcon = (summary) => {
    switch(summary) {
      case 'Freezing':
        return <Cloud className="me-2 text-primary" size={24} />;
      case 'Warm':
      case 'Balmy':
        return <Thermometer className="me-2 text-primary" size={24} />;
      default:
        return <Wind className="me-2 text-primary" size={24} />;
    }
  };

  const getTemperatureColor = (temperatureC) => {
    if (temperatureC < 0) return 'primary';
    if (temperatureC < 15) return 'info';
    if (temperatureC < 25) return 'success';
    return 'warning';
  };

  return (
    <Container className="py-5 mt-4">
      <Row className="g-4">
        <Col md={12}>
          <Card className="shadow-sm border-0 hover-card">
            <CardBody>
              <CardTitle tag="h4" className="d-flex align-items-center mb-3">
                <Wind className="me-2 text-primary" size={24} />
                Pronóstico del Tiempo
              </CardTitle>
              <p className="text-muted">
                Información meteorológica actualizada para los próximos días.
              </p>
            </CardBody>
          </Card>
        </Col>

        {loading && (
          <Col md={12}>
            <p>Cargando datos...</p>
          </Col>
        )}

        {error && (
          <Col md={12}>
            <Card className="bg-danger text-white">
              <CardBody>
                <p>Error al cargar el pronóstico: {error.message}</p>
              </CardBody>
            </Card>
          </Col>
        )}

        {weatherData && (
          <>
            {/* Weather Forecast Table */}
            <Col md={12}>
              <Card className="shadow-sm border-0">
                <CardBody>
                  <Table responsive striped hover>
                    <thead>
                      <tr>
                        <th>Fecha</th>
                        <th>Temperatura (°C)</th>
                        <th>Temperatura (°F)</th>
                        <th>Resumen</th>
                      </tr>
                    </thead>
                    <tbody>
                      {weatherData.map((forecast) => (
                        <tr key={forecast.date}>
                          <td>
                            {new Date(forecast.date).toLocaleDateString('es-ES', { 
                              weekday: 'long', 
                              day: 'numeric', 
                              month: 'long' 
                            })}
                          </td>
                          <td>
                            <Badge color={getTemperatureColor(forecast.temperatureC)}>
                              {forecast.temperatureC}°C
                            </Badge>
                          </td>
                          <td>{forecast.temperatureF}°F</td>
                          <td>{forecast.summary}</td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>

            {/* Detailed Forecast Cards */}
            {weatherData.map((forecast) => (
              <Col md={4} key={forecast.date}>
                <Card className="h-100 shadow-sm border-0 hover-card">
                  <CardBody>
                    <CardTitle tag="h4" className="d-flex align-items-center mb-3">
                      {getTemperatureIcon(forecast.summary)}
                      {new Date(forecast.date).toLocaleDateString('es-ES', { 
                        weekday: 'long', 
                        day: 'numeric', 
                        month: 'long' 
                      })}
                    </CardTitle>
                    <div className="d-flex align-items-center mb-2">
                      <Badge color={getTemperatureColor(forecast.temperatureC)} className="me-2">
                        {forecast.temperatureC}°C
                      </Badge>
                      <Badge color="info">{forecast.summary}</Badge>
                    </div>
                    <p className="text-muted">
                      Temperatura en Fahrenheit: {forecast.temperatureF}°F
                    </p>
                  </CardBody>
                </Card>
              </Col>
            ))}
          </>
        )}
      </Row>
    </Container>
  );
};

export default WeatherForecastPage;
