import axios from 'axios';

// URL base para la API
const API_URL = 'http://localhost:5199';

// Configuración base de axios
const baseConfig = {
  baseURL: API_URL,
  headers: {
    'Accept': 'application/json',
  }
};

// Instancia de axios
const api = axios.create(baseConfig);

// -------- Funciones relacionadas con WeatherForecast --------
export const obtenerClima = () => api.get('/WeatherForecast');

export const obtenerEmpleados = () => api.get('/Empleado');
export const obtenerEmpleadosPorId = (id) => api.get(`/Empleado/${id}`);
export const crearEmpleado = (data) => api.post('/empleado', data);
export const actualizarEmpleado = (id, data) => api.put(`/empleado/${id}`, data);
export const eliminarEmpleado = (id) => api.delete(`/empleado/${id}`);

// -------- Agregar más rutas según sea necesario --------


// Exportar la instancia para su uso en otros módulos
export { api };
export default api;
