        setLoading(false);
      } catch (err) {
        setError('No se pudieron cargar los datos del clima');
        setLoading(false);
      }
    };

    fetchWeatherData();
  }, []);

  return (
    <Container className="py-5 mt-4">
      <Row>
        <Col md={12}>
          <Card className="shadow-sm border-0">
            <CardBody>
              <CardTitle tag="h4" className="d-flex align-items-center mb-3">
                <Cloud className="me-2 text-primary" size={24} />
                Pron√≥stico del Clima
              </CardTitle>

              {error && (
                <Alert color="danger">
                  {error}
                </Alert>
              )}

              <Table responsive striped hover>
                <thead>
                  <tr>
                    <th>Fecha</th>
                    <th>Temperatura (¬∞C)</th>
                    <th>Temperatura (¬∞F)</th>
                    <th>Resumen</th>
                  </tr>
                </thead>
                <tbody>
                  {loading ? (
                    <tr>
                      <td colSpan="4" className="text-center">
                        Cargando datos del clima...
                      </td>
                    </tr>
                  ) : weatherData.length === 0 ? (
                    <tr>
                      <td colSpan="4" className="text-center">
                        No hay datos de clima disponibles
                      </td>
                    </tr>
                  ) : (
                    weatherData.map((forecast, index) => (
                      <tr key={index}>
                        <td>{forecast.date}</td>
                        <td>{forecast.temperatureC}¬∞C</td>
                        <td>{forecast.temperatureF}¬∞F</td>
                        <td>{forecast.summary}</td>
                      </tr>
                    ))
                  )}
                </tbody>
              </Table>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default WeatherForecastPage;
