import React, { useState } from 'react';
import { User, Edit2, Trash2, DollarSign, Search, Plus, AlertCircle, Check } from 'lucide-react';
import EmpleadoModal from '../EmpleadoModal';
import useEmpleados from '../hooks/useEmpleados';

const TablaEmpleados = () => {
  const {
    empleados,
    empleadoSeleccionado,
    loading,
    error,
    notification,
    fetchEmpleadoDetalle,
    createEmpleado,
    updateEmpleado,
    deleteEmpleado,
    setEmpleadoSeleccionado,
    formatSalario
  } = useEmpleados();

  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState('view'); // 'view', 'edit', 'create', 'delete'

  const handleOpenModal = async (mode, empleado = null) => {
    setModalMode(mode);
    
    if (mode === 'create') {
      setEmpleadoSeleccionado(null);
    } else if (empleado) {
      await fetchEmpleadoDetalle(empleado.empleadoId);
    }
    
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEmpleadoSeleccionado(null);
  };

  const handleSubmit = async (formData) => {
    let success = false;
    
    switch (modalMode) {
      case 'create':
        success = await createEmpleado(formData);
        break;
      case 'edit':
        success = await updateEmpleado(empleadoSeleccionado.empleadoId, {
          empleadoId: empleadoSeleccionado.empleadoId,
          ...formData
        });
        break;
      case 'delete':
        success = await deleteEmpleado(empleadoSeleccionado.empleadoId);
        break;
      default:
        break;
    }
    
    if (success) {
      handleCloseModal();
    }
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-6 flex flex-col md:flex-row md:items-center md:justify-between">
        <div className="flex items-center">
          <User size={24} className="text-blue-600 mr-2" />
          <h1 className="text-2xl font-bold text-gray-800">Administración de Empleados</h1>
        </div>
        
        <div className="mt-4 md:mt-0 flex items-center">
          <button
            onClick={() => handleOpenModal('create')}
            className="flex items-center py-2 px-4 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors"
          >
            <Plus size={20} className="mr-1" />
            Nuevo Empleado
          </button>
        </div>
      </div>
      
      {/* Notification */}
      {notification.show && (
        <div 
          className={`mb-4 p-3 rounded-lg flex items-center ${
            notification.type === 'success' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}
        >
          {notification.type === 'success' ? (
            <Check size={20} className="mr-2 text-green-600" />
          ) : (
            <AlertCircle size={20} className="mr-2 text-red-600" />
          )}
          <p>{notification.message}</p>
        </div>
      )}
      
      {/* Card container */}
      <div className="bg-white rounded-lg shadow-md border border-gray-200 overflow-hidden">
        {/* Table */}
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">ID</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Nombre</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Salario</th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Acciones</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {loading ? (
                <tr>
                  <td colSpan="4" className="px-6 py-4 text-center text-sm text-gray-500">
                    Cargando empleados...
                  </td>
                </tr>
              ) : error ? (
                <tr>
                  <td colSpan="4" className="px-6 py-4 text-center text-sm text-red-500">
                    {error}
                  </td>
                </tr>
              ) : empleados.length === 0 ? (
                <tr>
                  <td colSpan="4" className="px-6 py-4 text-center text-sm text-gray-500">
                    No hay empleados registrados
                  </td>
                </tr>
              ) : (
                empleados.map((empleado) => (
                  <tr key={empleado.empleadoId} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {empleado.empleadoId}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                      {empleado.nombre}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">
                      <div className="flex items-center">
                        <DollarSign size={16} className="text-green-600 mr-1" />
                        {formatSalario(empleado.salario)}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex justify-end space-x-2">
                        <button
                          onClick={() => handleOpenModal('view', empleado)}
                          className="p-1 text-blue-600 hover:text-blue-800 rounded-full hover:bg-blue-100"
                          title="Ver detalles"
                        >
                          <Search size={18} />
                        </button>
                        <button
                          onClick={() => handleOpenModal('edit', empleado)}
                          className="p-1 text-amber-600 hover:text-amber-800 rounded-full hover:bg-amber-100"
                          title="Editar empleado"
                        >
                          <Edit2 size={18} />
                        </button>
                        <button
                          onClick={() => handleOpenModal('delete', empleado)}
                          className="p-1 text-red-600 hover:text-red-800 rounded-full hover:bg-red-100"
                          title="Eliminar empleado"
                        >
                          <Trash2 size={18} />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
      
      {/* Modal */}
      <EmpleadoModal
        isOpen={showModal}
        onClose={handleCloseModal}
        mode={modalMode}
        empleado={empleadoSeleccionado}
        onSubmit={handleSubmit}
        formatSalario={formatSalario}
      />
    </div>
  );
};

export default TablaEmpleados;
