import React, { useState, useEffect } from 'react';
import { User, Edit2, DollarSign, X, AlertCircle, Check } from 'lucide-react';

const EmpleadoModal = ({ 
  isOpen, 
  onClose, 
  mode, 
  empleado, 
  onSubmit, 
  formatSalario 
}) => {
  const [formData, setFormData] = useState({
    nombre: '',
    salario: 0
  });

  useEffect(() => {
    if (empleado && (mode === 'view' || mode === 'edit' || mode === 'delete')) {
      setFormData({
        nombre: empleado.nombre,
        salario: empleado.salario
      });
    } else if (mode === 'create') {
      setFormData({
        nombre: '',
        salario: 0
      });
    }
  }, [empleado, mode]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'salario' ? parseFloat(value) || 0 : value
    });
  };

  const handleSubmit = () => {
    onSubmit(formData);
  };

  if (!isOpen) return null;

  const renderModalContent = () => {
    switch (mode) {
      case 'view':
        return empleado ? (
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md mx-auto">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-gray-800">Detalles del Empleado</h3>
              <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
                <X size={20} />
              </button>
            </div>
            
            <div className="bg-blue-50 p-4 rounded-lg mb-4">
              <div className="flex items-center justify-center mb-4">
                <div className="w-16 h-16 rounded-full bg-blue-100 flex items-center justify-center">
                  <User size={32} className="text-blue-600" />
                </div>
              </div>
              <h4 className="text-lg font-semibold text-center">{empleado.nombre}</h4>
            </div>
            
            <div className="space-y-4">
              <div className="border border-gray-200 rounded-lg p-4">
                <p className="text-sm text-gray-500">ID del Empleado</p>
                <p className="font-medium">{empleado.empleadoId}</p>
              </div>
              
              <div className="border border-gray-200 rounded-lg p-4">
                <div className="flex items-center">
                  <DollarSign size={18} className="text-green-600 mr-2" />
                  <p className="text-sm text-gray-500">Salario</p>
                </div>
                <p className="font-medium text-lg">{formatSalario(empleado.salario)}</p>
              </div>
            </div>
            
            <div className="mt-6 flex justify-end">
              <button 
                onClick={onClose}
                className="w-full py-2 px-4 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors"
              >
                Cerrar
              </button>
            </div>
          </div>
        ) : null;
        
      case 'edit':
      case 'create':
        return (
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md mx-auto">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-gray-800">
                {mode === 'create' ? 'Crear Nuevo Empleado' : 'Editar Empleado'}
              </h3>
              <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
                <X size={20} />
              </button>
            </div>
            
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Nombre</label>
                <input
                  type="text"
                  name="nombre"
                  value={formData.nombre}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  placeholder="Nombre del empleado"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Salario</label>
                <div className="relative">
                  <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <DollarSign size={18} className="text-gray-400" />
                  </div>
                  <input
                    type="number"
                    name="salario"
                    value={formData.salario}
                    onChange={handleInputChange}
                    className="w-full p-2 pl-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Salario del empleado"
                  />
                </div>
              </div>
            </div>
            
            <div className="mt-6 flex justify-end space-x-3">
              <button 
                onClick={onClose}
                className="py-2 px-4 border border-gray-300 text-gray-700 font-medium rounded-lg hover:bg-gray-50 transition-colors"
              >
                Cancelar
              </button>
              <button 
                onClick={handleSubmit}
                className="py-2 px-4 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors"
              >
                {mode === 'create' ? 'Crear' : 'Guardar Cambios'}
              </button>
            </div>
          </div>
        );
        
      case 'delete':
        return empleado ? (
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md mx-auto">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-xl font-bold text-gray-800">Eliminar Empleado</h3>
              <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
                <X size={20} />
              </button>
            </div>
            
            <div className="bg-red-50 p-4 rounded-lg mb-4 flex items-center">
              <AlertCircle size={24} className="text-red-600 mr-3" />
              <div>
                <h4 className="font-medium text-red-800">¿Está seguro?</h4>
                <p className="text-sm text-red-700 mt-1">
                  Esta acción no se puede deshacer. Se eliminará permanentemente a <strong>{empleado.nombre}</strong>.
                </p>
              </div>
            </div>
            
            <div className="mt-6 flex justify-end space-x-3">
              <button 
                onClick={onClose}
                className="py-2 px-4 border border-gray-300 text-gray-700 font-medium rounded-lg hover:bg-gray-50 transition-colors"
              >
                Cancelar
              </button>
              <button 
                onClick={handleSubmit}
                className="py-2 px-4 bg-red-600 text-white font-medium rounded-lg hover:bg-red-700 transition-colors"
              >
                Eliminar
              </button>
            </div>
          </div>
        ) : null;
        
      default:
        return null;
    }
  };

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
        <div className="fixed inset-0 transition-opacity" aria-hidden="true">
          <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
        </div>
        
        <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
        
        <div className="inline-block align-bottom sm:align-middle w-full max-w-md p-2">
          {renderModalContent()}
        </div>
      </div>
    </div>
  );
};

export default EmpleadoModal;
